let cloneDeep = require('lodash.clonedeep');

export class MockHttpClient {
  get = async (path: string, params: any): Promise<any> => {
    switch (path) {
      case GET_ROUTE.PING:
        return true
      case GET_ROUTE.SWAP_PING:
        return true
      case GET_ROUTE.FEES:
        return cloneDeep(FEE_INFO)
      case GET_ROUTE.COINS_INFO:
        return COINS_INFO
      case GET_ROUTE.ROOT:
        return cloneDeep(ROOT_INFO)
      case GET_ROUTE.STATECHAIN:
        switch (params) {
          case RECOVERY_TRANSFER_FINALIZE_DATA_API.statechain_id:
            return cloneDeep(RECOVERY_STATECHAIN_DATA)
          default:
            return cloneDeep(STATECHAIN_INFO)
        }
      case GET_ROUTE.TRANSFER_BATCH:
        return {
          status: true
        }
      case GET_ROUTE.SC_TRANSFER_FINALIZE_DATA:
        return cloneDeep(RECOVERY_TRANSFER_FINALIZE_DATA_API)
      case GET_ROUTE.TOKEN_INIT:
        return TOKEN_INIT 
      case GET_ROUTE.TOKEN_VERIFY:
        return TOKEN_VERIFY
    }
  }

  post = async (path: string, _body: any): Promise<any> => {
    switch (path) {
      case POST_ROUTE.KEYGEN_FIRST:
        return KEYGEN_FIRST;
      case POST_ROUTE.KEYGEN_SECOND:
        return KEYGEN_SECOND
      case POST_ROUTE.SIGN_FIRST:
        return SIGN_FIRST
      case POST_ROUTE.PREPARE_SIGN:
        return true
      case POST_ROUTE.SIGN_SECOND:
        return SIGN_SECOND
      case POST_ROUTE.SMT_PROOF:
        return SMT_PROOF
      case POST_ROUTE.DEPOSIT_INIT:
        return { id: "861d2223-7d84-44f1-ba3e-4cd7dd418560",
      challenge: "861d22237d8444f1ba3e4cd7dd418560" };
      case POST_ROUTE.POD_DEPOSIT_INIT:
        return { id: "861d2223-7d84-44f1-ba3e-4cd7dd418560"};
      case POST_ROUTE.DEPOSIT_CONFIRM:
        return DEPOSIT_CONFIRM
      case POST_ROUTE.WITHDRAW_INIT:
        return
      case POST_ROUTE.WITHDRAW_CONFIRM:
        return WITHDRAW_SIG
      case POST_ROUTE.TRANSFER_SENDER:
        return TRANSFER_SENDER
      case POST_ROUTE.TRANSFER_RECEIVER:
        return TRANSFER_RECEIVER
      case POST_ROUTE.TRANSFER_UPDATE_MSG:
        return
      case POST_ROUTE.TRANSFER_GET_MSG:
        return TRANSFER_PUBKEY
      case POST_ROUTE.SWAP_POLL_UTXO:
        return POLL_UTXO
      case POST_ROUTE.SWAP_POLL_SWAP:
        return POLL_UTXO
      case POST_ROUTE.RECOVER:
        return RECOVERY_STATECHAIN_DATA
      case POST_ROUTE.SWAP_DEREGISTER_UTXO:
        return
      case POST_ROUTE.TOR_ENDPOINTS:
        return
      case POST_ROUTE.SWAP_REGISTER_UTXO:
        return
      case  POST_ROUTE.SWAP_INFO:
        return
      case  POST_ROUTE.SWAP_FIRST:
        return
    }
  }

  new_tor_id = async () => {
  }
}


